package com.example.doctour.presentation.ui.adapter.aboutdoctorimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.example.doctour.databinding.ItemFeedbacksBindingimport com.example.doctour.presentation.model.review.ReviewUIclass AboutFeedbacksAdapter : RecyclerView.Adapter<AboutFeedbacksAdapter.FeedBacksViewHolder>() {    private var reviewList = arrayListOf<ReviewUI>()    fun addReview(list: List<ReviewUI>) {        reviewList.clear()        reviewList.addAll(list)        notifyDataSetChanged()    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): FeedBacksViewHolder {        return FeedBacksViewHolder(            ItemFeedbacksBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: FeedBacksViewHolder, position: Int) {        holder.bind(reviewList[position])    }    override fun getItemCount(): Int {        return reviewList.size    }    inner class FeedBacksViewHolder(private var binding: ItemFeedbacksBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(reviewUi: ReviewUI) {            binding.tvName.text = reviewUi.doctor.toString()            binding.tvFeedback.text = reviewUi.text            binding.tvStars.text = reviewUi.stars.toString()        }    }}class DFUtilFeedback: DiffUtil.ItemCallback<ReviewUI> (){    override fun areItemsTheSame(oldItem: ReviewUI, newItem: ReviewUI): Boolean {        return oldItem.id == newItem.id    }    override fun areContentsTheSame(oldItem: ReviewUI, newItem: ReviewUI): Boolean {        return oldItem==newItem    }}