package com.example.doctour.presentation.ui.adapter.category.categoryServiceimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.paging.PagingDataAdapterimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.example.doctour.databinding.ItemServicesBindingimport com.example.doctour.presentation.model.service.ServiceUiclass CategoryServicesAdapter(private val onClick:(ServiceUi)->Unit)    : PagingDataAdapter<ServiceUi, CategoryServicesAdapter.CategoryServiceViewHolder>(    CategoryServiceDFUtil()) {    inner class CategoryServiceViewHolder (        private val binding: ItemServicesBinding            ): RecyclerView.ViewHolder(binding.root){            fun bind(serviceUi: ServiceUi){                binding.tvServiceName.text=serviceUi.name                itemView.setOnClickListener{                    onClick.invoke(serviceUi)                }            }    }    override fun onBindViewHolder(holder: CategoryServiceViewHolder, position: Int) {         getItem(position)?.let { holder.bind(it) }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): CategoryServiceViewHolder {        return CategoryServiceViewHolder(            ItemServicesBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }}class CategoryServiceDFUtil: DiffUtil.ItemCallback<ServiceUi>(){    override fun areItemsTheSame(oldItem: ServiceUi, newItem: ServiceUi): Boolean {        return oldItem.slug == newItem.slug    }    override fun areContentsTheSame(oldItem: ServiceUi, newItem: ServiceUi): Boolean {        return  oldItem == newItem    }}