package com.example.doctour.presentation.ui.fragments.authAndRegimport com.example.doctour.preferences.UserPreferencesimport okhttp3.Interceptorimport okhttp3.Responseimport javax.inject.Injectclass AuthInterceptor @Inject constructor(    private val userDataPreferencesManager: UserPreferences,) : Interceptor {    override fun intercept(chain: Interceptor.Chain): Response {        val token = userDataPreferencesManager.accessToken        val request = chain.request()        if (!token.isNullOrEmpty()) {            val newRequest = request                .newBuilder()                .header("Authorization", "Bearer $token")                .build()            val response = chain.proceed(newRequest)            if (response.code == 401) {                //gg            } else {                return response            }        }        return chain.proceed(request)    }}